import React, { useState, useEffect } from 'react'

import Item from './Item'

export default function Basket(props) {

    const [total, setTotal] = useState(0)

    const { items } = props

    useEffect((props) => {
        calculateTotal()

    }, [total])

    const calculateTotal = () => {
        let total = 0
        if (items.length) {
            items.forEach(item => {
                total += item.price
            });
            console.log(total)
        }
        setTotal(total)
    }

    return (
        <div id='basket'>
            {!items.length ?
                <h2>Your shop basket is empty.</h2>
                :
                <>
                    <h2>Your Items:</h2>
                    {items.map(item => (
                        <Item
                            key={item.productId}
                            item={item}
                        />
                    ))}
                </>
            }
            <div className='total'>Total:{total}</div>
            <span onClick={() => props.setBasket(false)} className='x'>âœ–</span>
        </div>
    )
}
