import React, { useState } from 'react'

import data from './data/products.json'
import Card from './components/Card'
import Button from './components/Button'
import Basket from './components/Basket'

export default function Shop(props) {

    const [basket, setBasket] = useState(false)
    const [itemsCount, setItemsCount] = useState(0)
    const [basketItems, setBasketItems] = useState([])
    const [data, setData] = useState([])

    const addItem = (item) => {
        basketItems.forEach(basketItem => {
            if (basketItem.productId === item.productId) {
                basketItem.count++
                setItemsCount(itemsCount + 1)
                return
            }
        })
        setBasketItems([...basketItems, item])
        setItemsCount(itemsCount + 1)
    }

    const removeItem = id => {
        setBasketItems(basketItems.filter(item => item.productId !== id))
        setItemsCount(itemsCount - 1)
    }

    let cards = data.map(card => (
        <Card addItem={addItem} key={card.productId} card={card} />
    ))
    console.log(data)

    return (
        <>

            <header className='header'>
                <Button
                    class='basket'
                    click={() => setBasket(true)}
                    txt={`show basket (${!itemsCount ? 'empty' : itemsCount})`} />

                {basket ?
                    <Basket
                        removeItem={removeItem}
                        setBasket={setBasket}
                        items={basketItems} />
                    :
                    ''
                }
            </header>

            <div className='products grid'>
                {cards}
            </div>
        </>
    )
}
